import React, {useState} from 'react';

const useFlipCard = (initialState = true) => {
const [isFacingUp, setIsFacingUp] = useState(initialState);
  const flipCard = () => {
    setIsFacingUp(isUp => !isUp);
  };
  return [is, flipCard]
};

export default useFlipCard;
